import React, { useEffect, useRef } from 'react';
import EmblaCarousel from 'embla-carousel';
import './events.css';

const AttendEvent = () => {

    
   

    const emblaRef = useRef(null);
  const emblaApi = useRef(null);

  useEffect(() => {
    if (emblaRef.current) {
      emblaApi.current = EmblaCarousel(emblaRef.current, { loop: true });

      return () => emblaApi.current.destroy();
    }
  }, []);

  const scrollPrev = () => {
    if (emblaApi.current) emblaApi.current.scrollPrev();
  };

  const scrollNext = () => {
    if (emblaApi.current) emblaApi.current.scrollNext();
  };

    return (
        <>
         <div className="carousel">
      <div className="embla" ref={emblaRef}>
        <div className="embla__container">
          <div className="embla__slide" style={{ backgroundColor: 'red' }}>Slide 1</div>
          <div className="embla__slide" style={{ backgroundColor: 'blue' }}>Slide 2</div>
          <div className="embla__slide" style={{ backgroundColor: 'green' }}>Slide 3</div>
        </div>
      </div>
      <button className="carousel-button prev" onClick={scrollPrev}>❮</button>
      <button className="carousel-button next" onClick={scrollNext}>❯</button>
    </div>
    

        </>
    );
}
export default AttendEvent;
