import React, { useState } from 'react';
import '../login/login.css';


const Shares = () => {


    const [action, setAction] = useState("Login");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const   
 [name, setName] = useState(""); // Add name if needed for signup
  const [errorMessage, setErrorMessage] = useState(null);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event)  => {
    setPassword(event.target.value);
  };

  const handleNameChange = (event)  => { // Add if needed for signup
    setName(event.target.value);
  };

  const handleLogin = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    // Implement login logic using your backend service
    // This is a placeholder, replace with your actual login functionality
    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok)   
 {
        throw new Error("Login failed");
      }

      // Handle successful login (e.g., redirect to another page)
      console.log("Login successful!");
    } catch (error) {
      setErrorMessage("Login failed. Please check your email and password.");
    }
  };

  const handleSignup = async (event) => {
    event.preventDefault(); // Prevent default form submission behavior

    // Implement signup logic using your backend service
    // This is a placeholder, replace with your actual signup functionality
    try {
      const response = await fetch("/api/signup", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password,   
 name }), // Add name if needed
      });

      if (!response.ok) {
        throw new Error("Signup failed");
      }

      // Handle successful signup (e.g., display success message)
      console.log("Signup successful!");
    } catch (error) {
      setErrorMessage("Signup failed. Please try again.");
    }
  };

    return (

        <>
            <div className="container">
            <div className="header">
                <div className="text">{action}</div>
            </div>
            <form className="inputs" onSubmit={handleSubmit}>
                {action === "Sign Up" && (
                    <div className="input">
                        <input
                            type="text"
                            placeholder="Name: John Doe"
                            value={name}
                            onChange={e => setName(e.target.value)}
                        />
                    </div>
                )}

                <div className="input">
                    <input
                        type="email"
                        placeholder="Email: user@gmail.com"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                </div>

                <div className="input">
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                    />
                </div>

                {action === "Sign Up" && (
                    <div className="input">
                        <input
                            type="password"
                            placeholder="Confirm Password"
                            value={confirmPassword}
                            onChange={e => setConfirmPassword(e.target.value)}
                        />
                    </div>
                )}

                {action === "Login" && (
                    <div className="forgot-password">Forgot Password?</div>
                )}

                <div className="login-signup">
                    {action === "Login" ? (
                        <button type="submit" className="button">Login</button>
                    ) : (
                        <button type="submit" className="button">Sign Up</button>
                    )}
                    <button
                        type="button"
                        className="button grey"
                        onClick={() => setAction(action === "Login" ? "Sign Up" : "Login")}
                    >
                        Switch to {action === "Login" ? "Sign Up" : "Login"}
                    </button>
                </div>
            </form>
        </div>
        
        </>
    );
}

export default Shares
