import React, { useEffect } from 'react';
import useEmblaCarousel from 'embla-carousel-react';
import './events.css';

const AttendEvent = () => {

    
   

    const [emblaRef, emblaApi] = useEmblaCarousel({ loop: false })


          useEffect(() => {
            if (emblaApi) {
                // Automatically initialize to the first slide or any specific slide
                emblaApi.scrollTo(0)
            }
        }, [emblaApi])
    
        const handlePrev2 = () => {
          
            if (emblaApi) {
                emblaApi.scrollPrev();
            }
        };


    return (
        <>
         <div>
      <div className="embla" ref={emblaRef}>
        <div className="embla__viewport">
          <div className="embla__container">
            <div className="embla__slide" style={{ backgroundColor: 'red' }}>Slide 1</div>
            <div className="embla__slide" style={{ backgroundColor: 'blue' }}>Slide 2</div>
            <div className="embla__slide" style={{ backgroundColor: 'green' }}>Slide 3</div>
          </div>
        </div>
      </div>
      {/* Attach the functions to the buttons */}
      <button
        className="embla-button-prev"
        onClick={handlePrev}
        disabled={!emblaApi || !emblaApi.canScrollPrev()}
      >
        ❮
      </button>
      <button
        className="embla-button-next"
        onClick={handleNext}
        disabled={!emblaApi || !emblaApi.canScrollNext()}
      >
        ❯
      </button>
    </div>
        </>
    );
}
export default AttendEvent;
