import React, { useCallback } from 'react'
import useEmblaCarousel from 'embla-carousel-react'
import Festival from '../assets/images/nairobi-mega-festival-1200x1600.webp';
import Queens from '../assets/images/queens-of-jazz-2020-785x785.webp';
import Hiphop from '../assets/images/screenshot-2024-05-13-at-06.06.03-715x714.webp';
import './events.css';

const AttendEvent = () => { 
    
    const [emblaRef, emblaApi] = useEmblaCarousel({loop: true})

  const scrollPrev = useCallback(() => {
    if (emblaApi) emblaApi.scrollPrev()
  }, [emblaApi])

  const scrollNext = useCallback(() => {
    if (emblaApi) emblaApi.scrollNext()
  }, [emblaApi])


    return (
        <>
         <div className="embla">
      <div className="embla__viewport" ref={emblaRef}>
        <div className="embla__container">
          <div className="embla__slide">{Festival}</div>
          <div className="embla__slide">{Queens}</div>
          <div className="embla__slide">{Hiphop}</div>
        </div>
      </div>
      <button className="embla__prev" onClick={scrollPrev}>
        Prev
      </button>
      <button className="embla__next" onClick={scrollNext}>
        Next
      </button>
    </div>

        </>
    );
}
export default AttendEvent;
