import React, {useState, useEffect, useCallback } from 'react';
import useEmblaCarousel from 'embla-carousel-react';
import './events.css';

const AttendEvent = () => {

    
   

    const [emblaRef, emblaApi] = useEmblaCarousel({ loop: true });
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = ['Slide 1', 'Slide 2', 'Slide 3'];

  useEffect(() => {
    if (emblaApi) {
      console.log('Embla API is ready');
      emblaApi.scrollTo(0);
      emblaApi.on('select', () => {
        console.log('Current index:', emblaApi.selectedScrollSnap());
        setCurrentSlide(emblaApi.selectedScrollSnap());
      });
    }
  }, [emblaApi]);

  const handlePrev = useCallback(() => {
    console.log('Prev button clicked');
    if (emblaApi) {
      emblaApi.scrollPrev();
      setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
    }
  }, [emblaApi, slides.length]);

  const handleNext = useCallback(() => {
    console.log('Next button clicked');
    if (emblaApi) {
      emblaApi.scrollNext();
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }
  }, [emblaApi, slides.length]);

    return (
        <>
         <div className="embla" ref={emblaRef}>
        <div className="embla__viewport">
          <div className="embla__container">
            {slides.map((slide, index) => (
              <div
                className="embla__slide"
                style={{ backgroundColor: index % 2 === 0 ? 'red' : index % 3 === 0 ? 'blue' : 'green' }}
                key={index}
              >
                {slide}
              </div>
            ))}
          </div>
        </div>
      </div>
      <button className="embla-button-prev" onClick={handlePrev}>
        ❮
      </button>
      <button className="embla-button-next" onClick={handleNext}>
        ❯
      </button>
    

        </>
    );
}
export default AttendEvent;
