import React from 'react';
import './events.css';

const AttendEvent = () => { 
    
    const emblaRef = useRef(null);
    const emblaApi = useRef(null);

    useEffect(() => {
        if (emblaRef.current) {
            emblaApi.current = EmblaCarousel(emblaRef.current, {
                loop: true,
                align: 'center',
                draggable: true,
                autoPlay: true,
                autoPlayInterval: 5000
            });

            return () => emblaApi.current && emblaApi.current.destroy();
        }
    }, []);


    return (
        <>
        <section className="slider4 mbr-embla" id="slider04-d">
            <div className="container-fluid">
                <div className="row mb-5 justify-content-center">
                    <div className="col-12">
                        <h3 className="mbr-section-title align-center mb-0 display-2"><strong>Past Events</strong></h3>
                        <h5 className="mbr-section-subtitle align-center mb-0 mt-4 display-7">Since May 2024...</h5>
                    </div>
                </div>
                <div className="row">
                    <div className="col-12">
                        <div className="embla position-relative" ref={emblaRef}>
                            <div className="embla__viewport">
                                <div className="embla__container">
                                    {[
                                        {src: 'assets/images/nairobi-mega-festival-1200x1600.webp', alt: 'Event Image', title: 'Nairobi Mega Festival'},
                                        {src: 'assets/images/queens-of-jazz-2020-785x785.webp', alt: 'Event Image', title: 'Queens of Jazz 2020'},
                                        {src: 'assets/images/screenshot-2024-05-13-at-06.06.03-715x714.webp', alt: 'Event Image', title: 'Comedy Night'},
                                        // Add more images here
                                    ].map((image, index) => (
                                        <div key={index} className="embla__slide slider-image item" style={{marginLeft: '1rem', marginRight: '1rem'}}>
                                            <a href="#">
                                                <div className="slide-content">
                                                    <div className="item-img">
                                                        <div className="item-wrapper">
                                                            <img src={image.src} alt={image.alt} title={image.title} />
                                                        </div>
                                                    </div>
                                                </div>
                                            </a>
                                        </div>
                                    ))}
                                </div>
                            </div>
                            <button className="embla__button embla__button--prev">
                                <span className="icon-prev" aria-hidden="true"></span>
                                <span className="sr-only">Previous</span>
                            </button>
                            <button className="embla__button embla__button--next">
                                <span className="icon-next" aria-hidden="true"></span>
                                <span className="sr-only">Next</span>
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </section>

        </>
    );
}
export default AttendEvent;
