import React, { useState } from 'react';
import './login.css'; // Import your CSS file

const Login = () => {
  
    const [action, setAction] = useState ("Login")

  return (
    <div className = "container">
        <div className = "header">
        <div className = "text">{action}</div>
            </div> 
            <div className = "inputs">
               {action === "Login" ? <div></div> : <div className = "input">
             <input type = "text" placeholder = "Name: John Doe" />   
                </div>} 
            
                <div className = "input">
             <input type = "email" placeholder = "Email: user@gmail.com" />
             </div>
             <div className = "input">
             <input type = "password" placeholder = "Password" />
             </div>
             {action === "Login" ? <div></div> : <div className = "input">
             <input type = "password" placeholder = " Confirm Password" />
             </div>}
              
                </div> 

                {action === "Sign Up" ? <div></div> : <div className = "forgot-password"> Forgot Password?</div>}

                <div className = "loginsingup">
                <div className={action === "Login" ? "button grey" : "button"} 
                onClick={() => {setAction("Sign Up")}}>
                <button type="submit">Sign Up</button>
                </div>
                <div className = {action=== "Sign Up" ? "button grey": "button"}  
                onClick={() => {setAction("Login")}}> 
                <button type= "submit">Login</button> 
                </div>
                </div> 

    </div>
    
  

);
};

export default Login;
