import React, { useEffect } from 'react';
import useEmblaCarousel from 'embla-carousel-react';
import './events.css';

const AttendEvent = () => {

    
    const [emblaRef, emblaApi] = useEmblaCarousel({ loop: true });

    useEffect(() => {
      if (emblaApi) {
        console.log('Embla API is ready');
        emblaApi.on('select', () => {
          console.log('Current index:', emblaApi.selectedScrollSnap());
        });
      }
    }, [emblaApi]);
  
    // Define handlePrev and handleNext functions inside the component
    const handlePrev = () => {
      console.log('Prev button clicked');
      if (emblaApi) {
        emblaApi.scrollPrev();
      }
    };
  
    const handleNext = () => {
      console.log('Next button clicked');
      if (emblaApi) {
        emblaApi.scrollNext();
      }
    };

    return (
        <>
         <div>
      <div className="embla" ref={emblaRef}>
        <div className="embla__viewport">
          <div className="embla__container">
            <div className="embla__slide" style={{ backgroundColor: 'red' }}>Slide 1</div>
            <div className="embla__slide" style={{ backgroundColor: 'blue' }}>Slide 2</div>
            <div className="embla__slide" style={{ backgroundColor: 'green' }}>Slide 3</div>
          </div>
        </div>
      </div>
      {/* Attach the functions to the buttons */}
      <button className = "embla-button-prev" onClick={handlePrev2} disabled={!emblaApi || !emblaApi.canScrollPrev()}> ❮ </button>
    <button className = "embla-button-next" onClick={handleNext2} disabled={!emblaApi || !emblaApi.canScrollNext()}> ❯ </button>
    </div>
        </>
    );
}
export default AttendEvent;
